import Head from "next/head";
import Link from 'next/link'
import ActiveLink from './ActiveLink'
import { useState } from 'react'


const Layout = (props) => {
  const [menu, setMenu] = useState('none')

  const onBurgerClick = () =>{
    if (menu === 'none'){
      setMenu('')
    }
    else {
      setMenu('none')
    }
    console.log(menu)
  }

  return (
    <div>
      <Head>
        <title>Portfolio</title>
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <meta charSet="utf-8" />
        <link href="https://fonts.googleapis.com/css?family=Nunito:400,400i,700,700i&display=swap" rel="stylesheet" />
      </Head>
      <nav>
        <div className='logo'>
          <img className='headshot' src='/headshot.jpg' />
          <div className='personal-info-container'>
            <div className='personal-info name'>
            <Link href='/'>
              <a>
                Noah Maizels
              </a>
            </Link>
            </div>
            <div className='personal-info job-title'>
              Web Developer
            </div>
          </div>
        </div>
        <div className='nav-menu'>
          <div className='links'>
            <ActiveLink href="/">
              Home
            </ActiveLink>
            <ActiveLink href="/projects">
              Projects
            </ActiveLink>
            <ActiveLink href="/contact">
              Contact
            </ActiveLink>
          </div>
          <img onClick={onBurgerClick} className='burger' src='burger-bar.png'/>
        </div>
      </nav>

      <div className="Content">{props.children}</div>
    <style jsx>{`
      .links {
        display: ${menu};
        position: absolute;
        transform: translate(0px, 100px);
        border: 2px solid #984B48;
        padding: 15px;
        padding-right: 50px;
        width: 90vw;
        background-color: white;
      }
      .nav-menu {
        display: flex;
        min-width: 150px;
        justify-content: flex-end;
        flex-direction: row;
        align-items: center;
      }
      nav {
        display: flex;
        justify-content: space-between;
        margin-bottom: 30px;
        align-items: center;
      }
      .logo {
        display: flex;
        align-items: flex-end;
        min-width: 200px;
        flex-wrap: nowrap;
      }
      .headshot {
        width: 50px;
        border-radius: 100%;
      }
      .personal-info-container{
        display: inline-block;
        margin-left: 15px;
      }
      .personal-info {
        line-height: 1rem;
      }
      .job-title {
        margin-top: 5px;
        color: #CCCCCC;
      }
      .burger {
        width: 30px;
        height: 30px;
      }
    `}</style>
    </div>
  )
}

export default Layout
